            data.url = $_GET['server'];

        this.socket = new WebSocket(app.wsURL(data.url, ENV.wsPort, ENV.wssPort));

        this.socket.binaryType = "arraybuffer";

        this.socket.addEventListener("open", this.onconnect.bind(this));
