        var sprites = ["run", "cast", "kick", "roll", "spin", "block", "growl", "throw", "punch1", "punch2", "stunned", "fall_back", "fall_front", "charge_meele", "charge_spell", "attack_vertical", "attack_horizontal"];

        var citizen = this.player;

