var Emitter = function() {

    this.map = new Map;

};

Emitter.prototype = {

    trigger(name) {

        let args = Array.from(arguments);

        args.shift();

        name = name.toLowerCase();

        let callbacks = this.map.get(name);

        if (!callbacks)
            return;

        for (let i = 0; i < callbacks.length; i++) {

            let callback = callbacks[i];

            callback(...args);

            if (callback.once)
                callbacks.splice(i--, 1);

        }

    },

    has(name) {

        let callbacks = this.map.get(name);

        return callbacks && callbacks.length > 0;

    },

    on(name, callback) {

        name = name.toLowerCase();

        if (!this.map.has(name))
            this.map.set(name, []);

        this.map.get(name).push(callback);

        return callback;

    },

    once(name, callback) {

        name = name.toLowerCase();

        if (!this.map.has(name))
            this.map.set(name, []);

        callback.once = true;

        this.map.get(name).push(callback);

        return callback;

    },

    off(name, callback) {

        let callbacks = this.map.get(name);

        if (!callbacks)
            return;

        let index = callbacks.indexOf(callback);

        if (index > -1)
            callbacks.splice(index, 1);

    },

    listen(name, callback, object) {

        if (!object.listeners)
            object.listeners = [];

        object.listeners.push([name, callback]);

        this.on(name, callback);

    },

    kill(object) {

        if (!object)
            return;
        if (!object.listeners)
            return;

        for (var i = 0; i < object.listeners.length; i++) {

            let listener = object.listeners[i];

            this.off(listener[0], listener[1]);

        }

    }

};

