        var frame = Math.abs(this.frame * 0.04 | 0) % 4;

        layer.save();
        layer.translate(this.x, this.y - this.z - 5);
        layer.scale(this.scale * this.scaleX, this.scale * this.scaleY);
        layer.drawImage(app.images.ball, frame * 16, 0, 16, 16, -8, -8, 16, 16);
